mapper.select:
SELECT 

FROM sicmed_asked_branch a

WHERE a.id = #{id}

SELECT 

FROM sicmed_asked_branch a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sicmed_asked_branch a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sicmed_asked_branch SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
