mapper.select:
SELECT 

FROM sicmed_asked_class a



	a.id = #{id}


	AND a.class_number = #{classNumber}

SELECT 

FROM sicmed_asked_class a

WHERE a.asked_calss = #{askedCalss}

SELECT 

FROM sicmed_asked_class a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.asked_calss = #{askedCalss}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sicmed_asked_class a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.asked_calss LIKE CONCAT ('%',#{askedCalss},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sicmed_asked_class a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sicmed_asked_class SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
