mapper.select:
SELECT 

FROM sicmed_base_code a

WHERE a.id = #{id}

SELECT 

FROM sicmed_base_code a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.code_business = #{codeBusiness}


		AND a.code_buns = #{codeBuns}


		AND a.code = #{code}


		AND a.code_name = #{codeName}


		AND a.code_extand = #{codeExtand}


		AND a.code_date = #{codeDate}


		AND a.code_use = #{codeUse}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sicmed_base_code a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sicmed_base_code SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
