		AND a.check_price_insert_date BETWEEN #{beginCheckPriceInsertDate} AND #{endCheckPriceInsertDate}


		AND a.check_code = #{sicmedTwxrayCheck.id}


		AND a.check_price = #{checkPrice}




		ORDER BY ${page.orderBy}


