mapper.update:
UPDATE sicmed_check_price SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT 

FROM sicmed_check_price a



		AND a.id = #{id}


		AND a.check_price_cutoff_date BETWEEN #{beginCheckPriceCutoffDate} AND #{endCheckPriceCutoffDate}


		AND a.check_price_insert_date BETWEEN #{beginCheckPriceInsertDate} AND #{endCheckPriceInsertDate}


		AND a.check_code = #{sicmedTwxrayCheck.id}


		AND a.check_price = #{checkPrice}




		AND b.check_name LIKE CONCAT('%',#{sicmedTwxrayCheck.checkName},'%')



		AND a.check_price_insert_date > #{startDate}



		AND a.check_price_cutoff_date < #{endDate}



		AND a.del_flag = #{delFlag}





		ORDER BY ${page.orderBy}


		ORDER BY a.check_price_insert_date DESC
