mapper.select:
SELECT 

FROM sicmed_cure_class a

WHERE a.id = #{id}

SELECT 

FROM sicmed_cure_class a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.class_code = #{classCode}


		AND a.class_name = #{className}


		AND a.pacs_class = #{pacsClass}


		AND a.sort = #{sort}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sicmed_cure_class a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sicmed_cure_class SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
