mapper.select:
SELECT 

FROM sicmed_cure_price a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.cure_code = #{sicmedCureCheck.id}


		AND a.price = #{price}


		AND a.price_cutoff_date = #{priceCutoffDate}


		AND a.price_insert_date = #{priceInsertDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sicmed_cure_price a

WHERE a.id = #{id}

SELECT 

FROM sicmed_cure_price a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.cure_code = #{sicmedCureCheck.id}




		ORDER BY a.price_insert_date DESC

SELECT 

FROM sicmed_cure_price a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.cure_code = #{sicmedCureCheck.id}


		AND a.price = #{price}


		AND b.cure_name = #{sicmedCureCheck.cureName}


		AND a.price_insert_date > #{startDate}


		AND a.price_insert_date < #{endDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sicmed_cure_price a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sicmed_cure_price SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT 

FROM sicmed_cure_price a



		AND a.cure_code = #{sicmedCureCheck.id}


		AND a.price = #{price}


		AND b.cure_name LIKE CONCAT('%',#{sicmedCureCheck.cureName},'%')


		AND a.price_insert_date > #{startDate}


		AND a.price_insert_date < #{endDate}


		AND a.del_flag = #{delFlag}





		ORDER BY a.price_insert_date DESC
