mapper.select:
SELECT 

FROM sicmed_dept a

WHERE a.id = #{id}

SELECT 

FROM sicmed_dept a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.dept_number = #{deptNumber}


		AND a.dept_name = #{deptName}


		AND a.dept_pmpa = #{deptPmpa}


		AND a.dept_code = #{deptCode}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sicmed_dept a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.dept_number LIKE CONCAT('%',#{deptNumber},'%' )


		AND a.dept_name LIKE CONCAT('%',#{deptName},'%' )


		AND a.dept_pmpa = #{deptPmpa}


		AND a.dept_code = #{deptCode}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sicmed_dept a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sicmed_dept SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
