mapper.select:
SELECT 

FROM sicmed_disease_class a


a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.remarks = #{remarks}

SELECT 

FROM sicmed_disease_class a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}



		AND a.disease_class_name LIKE = #{diseaseClassName}


		AND a.disease_class_code = #{diseaseClassCode}


		AND a.create_by = #{createBy.id}


		AND a.create_date = #{createDate}


		AND a.update_by = #{updateBy.id}


		AND a.update_date = #{updateDate}


		AND a.remarks = #{remarks}


		AND a.del_flag = #{delFlag}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sicmed_disease_class a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}



		AND a.disease_class_name LIKE CONCAT ('%',#{diseaseClassName},'%')


		AND a.disease_class_code LIKE CONCAT ('%',#{diseaseClassCode},'%')


		AND a.create_by = #{createBy.id}


		AND a.create_date = #{createDate}


		AND a.update_by = #{updateBy.id}


		AND a.update_date = #{updateDate}


		AND a.remarks = #{remarks}


		AND a.del_flag = #{delFlag}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sicmed_disease_class a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sicmed_disease_class SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
