mapper.select:
SELECT

FROM sicmed_disease a

WHERE a.id = #{id}

SELECT

FROM sicmed_disease a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.disease_class_code = #{diseaseClass.id}


		AND d.disease_class_name = #{diseaseClass.diseaseClassName}



		AND a.disease_name LIKE CONCAT ('%',#{diseaseName},'%') 


		AND a.disease_name_english = #{diseaseNameEnglish}


		AND a.disease_name_pmpa = #{diseaseNamePmpa}



		AND a.disease_code LIKE CONCAT ('%',#{diseaseCode},'%') 


		AND a.create_by = #{createBy.id}


		AND a.create_date = #{createDate}


		AND a.update_by = #{updateBy.id}


		AND a.update_date = #{updateDate}


		AND a.remarks = #{remarks}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM sicmed_disease a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.disease_class_code = #{diseaseClass.id}


		AND d.disease_class_name = #{diseaseClass.diseaseClassName}



		AND a.disease_name = #{diseaseName}


		AND a.disease_name_english = #{diseaseNameEnglish}


		AND a.disease_name_pmpa = #{diseaseNamePmpa}



		AND a.disease_code = #{diseaseCode} 


		AND a.create_by = #{createBy.id}


		AND a.create_date = #{createDate}


		AND a.update_by = #{updateBy.id}


		AND a.update_date = #{updateDate}


		AND a.remarks = #{remarks}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM sicmed_disease a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sicmed_disease SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
