mapper.select:
SELECT 

FROM sicmed_entrusted_asked a

WHERE a.id = #{id}

SELECT 

FROM sicmed_entrusted_asked a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.order_code = #{orderCode}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sicmed_entrusted_asked a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
