mapper.select:
SELECT 

FROM sicmed_hospital a

WHERE a.hospital_id = #{hospitalId}

SELECT 

FROM sicmed_hospital a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.hospital_id = #{hospitalId}


		AND a.hospital_name = #{hospitalName}


		AND a.hospital_grade = #{hospitalGrade}


		AND a.hospital_add = #{hospitalAdd}


		AND a.city_code = #{sicmedCity.cityId}


		AND a.create_by = #{createBy.id}


		AND a.create_date = #{createDate}


		AND a.update_by = #{updateBy.id}


		AND a.update_date = #{updateDate}


		AND a.remarks = #{remarks}


		AND a.del_flag = #{delFlag}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sicmed_hospital a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sicmed_hospital SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
