mapper.update:
UPDATE sicmed_inspect_price SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT 

FROM sicmed_inspect_price a




		AND a.inspect_code = #{sicmedInspect.id}


		AND a.price = #{price}


		AND a.price_cutoff_date = #{priceCutoffDate}


		AND a.price_insert_date = #{priceInsertDate}


		AND a.create_by = #{createBy.id}


		AND a.create_date = #{createDate}


		AND b.inspect_name LIKE CONCAT('%',#{sicmedInspect.inspectName},'%') 


		AND a.price_insert_date > #{startDate}


		AND a.price_insert_date < #{endDate}


		AND a.del_flag = #{delFlag}





		ORDER BY ${page.orderBy}


		ORDER BY a.price_insert_date DESC
