mapper.select:
SELECT 

FROM sicmed_medical_asked a

WHERE a.id = #{id}

SELECT 

FROM sicmed_medical_asked a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.medical_code = #{medicalCode}


		AND a.asked_code = #{askedCode}


		AND a.remarks = #{remarks}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sicmed_medical_asked a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sicmed_medical_asked SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
