mapper.select:
SELECT 

FROM sicmed_medical_class a




		AND a.id = #{id}


		AND a.name =#{name};

SELECT 

FROM sicmed_medical_class a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.code =#{code}


		AND a.parent_ids = #{parentIds}


		AND a.parent_id = #{parent.id}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.sort LIKE CONCAT('%',#{sort},'%')  


		AND a.pace LIKE CONCAT('%', #{pace},'%') 




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sicmed_medical_class a


	a.del_flag = #{DEL_FLAG_NORMAL}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sicmed_medical_class SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
