mapper.select:
SELECT 

FROM sicmed_medical_use a

WHERE a.id = #{id}

SELECT 

FROM sicmed_medical_use a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.asked_code = #{askedCode}


		AND a.use_group = #{useGroup}


		AND a.use_name = #{useName}


		AND a.use_bun = #{useBun}


		AND a.use_unit = #{useUnit}


		AND a.use_amount = #{useAmount}


		AND a.use_day = #{useDay}


		AND a.use_bout = #{useBout}


		AND a.create_user = #{createUser}


		AND a.update_user = #{updateUser}


		AND a.create_by = #{createBy.id}


		AND a.create_date = #{createDate}


		AND a.update_by = #{updateBy.id}


		AND a.update_date = #{updateDate}


		AND a.remarks = #{remarks}


		AND a.del_flag = #{delFlag}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sicmed_medical_use a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sicmed_medical_use SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
