mapper.select:
SELECT 

FROM sicmed_patient_asked a

WHERE a.id = #{id}

SELECT 

FROM sicmed_patient_asked a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.patient_code = #{sicmedPatient.id}


		AND a.asked_code = #{sicmedAsked.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sicmed_patient_asked a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.insert:
INSERT INTO sicmed_patient_asked(
	 patient_code,
	 asked_code,
	 medical_code
)VALUES (
	#{sicmedPatient.id},
	#{sicmedAsked.id},
	#{sicmedMedical.id}
)

mapper.update:
UPDATE sicmed_patient_asked SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
