mapper.select:
SELECT

FROM sicmed_patient_disease a

WHERE a.id = #{id}

SELECT

FROM sicmed_patient_disease a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.disease_code = #{diseaseCode.id}


		AND a.case_code = #{caseCode.id}

SELECT

FROM sicmed_patient_disease a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.patient_code = #{patientCode.id}


		AND a.disease_code = #{diseaseCode.id}


		AND a.branch_code = #{branchCode.id}


		AND a.case_code = #{caseCode.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM sicmed_patient_disease a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sicmed_patient_disease SET
del_flag =
#{DEL_FLAG_DELETE}
WHERE id = #{id}
