mapper.select:
SELECT 

FROM sicmed_patient_price a

WHERE a.id = #{id}

SELECT 

FROM sicmed_patient_price a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.patient_code = #{sicmedPatient.id}


		AND a.price_class_type = #{priceClassType}


		AND a.price_code = #{priceCode}


		AND a.price = #{price}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sicmed_patient_price a

WHERE a.patient_code = #{sicmedPatient.id}

		AND a.price_code = #{priceCode}



		AND a.price_class_type = #{priceClassType}


		AND a.isPay = #{isPay}

SELECT 

FROM sicmed_patient_price a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.patient_code = #{sicmedPatient.id}


		AND a.price_class_type = #{priceClassType}


		AND a.price_code = #{priceCode}


		AND a.isPay = #{isPay}




		ORDER BY a.price_class_type ASC

SELECT 

FROM sicmed_patient_price a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.patient_code = #{sicmedPatient.id}


		AND a.price_class_type = #{priceClassType}


		AND a.price_code = #{priceCode}


		AND a.isPay = #{isPay}


		AND b.patient_card = #{sicmedPatient.patientCard}



		AND b.patient_name LIKE CONCAT('%',#{sicmedPatient.patientName},'%')


		AND b.patient_number LIKE CONCAT('%',#{sicmedPatient.patientNumber},'%')


		AND c.user_name = #{createUser.userName}


		AND a.create_date  > #{startDate}


		AND a.create_date < #{endDate}


		AND b.patient_name = #{sicmedPatient.searchContent} OR b.patient_number = #{sicmedPatient.searchContent}


		GROUP BY a.patient_code




		ORDER BY ${page.orderBy}


		ORDER BY a.patient_code DESC

SELECT 

FROM sicmed_patient_price a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.patient_code = #{sicmedPatient.id}


		AND a.price_class_type = #{priceClassType}


		AND a.price_code = #{priceCode}


		AND a.isPay = #{isPay}


		AND b.patient_card = #{sicmedPatient.patientCard}



		AND b.patient_name = #{sicmedPatient.patientName}


		AND c.user_name = #{createUser.userName}


		AND a.create_date  > #{startDate}


		AND a.create_date < #{endDate}


		AND b.patient_name = #{sicmedPatient.searchContent} OR b.patient_number = #{sicmedPatient.searchContent}


		GROUP BY a.patient_code




		ORDER BY ${page.orderBy}


		ORDER BY a.patient_code DESC

SELECT 

FROM sicmed_patient_price a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sicmed_patient_price SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE price_class_type = #{priceClassType}
	AND patient_code = #{sicmedPatient.id}

UPDATE sicmed_patient_price SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT 

FROM sicmed_patient_price a

	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.patient_code = #{sicmedPatient.id}


		AND a.isPay = #{isPay}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sicmed_patient_price a



		AND a.del_flag = #{delFlag}


		AND a.isPay = #{isPay}


		AND a.create_user = #{createUser.id}


		AND a.update_user = #{updateUser.id}


		AND a.update_date  > #{startDate}


		AND a.update_date < #{endDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC
