mapper.select:
SELECT

FROM sicmed_registered_price a

WHERE a.id = #{id}

SELECT

FROM sicmed_registered_price a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.code = #{sicmedRegisteredType.id}


		AND b.name_china = #{sicmedRegisteredType.nameChina}


		AND a.registered_fee = #{registeredFee}


		AND a.clinics_fee = #{clinicsFee}


		AND a.material_fee = #{materialFee}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM sicmed_registered_price a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sicmed_registered_price SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
