mapper.select:
SELECT 

FROM sicmed_registered_type a

WHERE a.id = #{id}

SELECT 

FROM sicmed_registered_type a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.code_grades <= #{codeGrades}


		AND a.price_id = #{sicmedRegisteredPrice.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sicmed_registered_type a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.code_grades <= #{codeGrades}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sicmed_registered_type a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name_china = #{nameChina}


		AND a.name_english = #{nameEnglish}


		AND a.price_id = #{sicmedRegisteredPrice.id}


		AND a.price_id = #{sicmedRegisteredPrice.materialFee}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sicmed_registered_type a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sicmed_registered_type SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
