mapper.select:
SELECT 

FROM sicmed_standardprescription_branch a

WHERE a.id = #{id}

SELECT 

FROM sicmed_standardprescription_branch a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.branch_code = #{sicmedBranch.id}


		AND a.asked_code = #{askedClass.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sicmed_standardprescription_branch a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sicmed_standardprescription_branch SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
