mapper.select:
SELECT 

FROM sicmed_standardprescription_doctor a

WHERE a.id = #{id}

SELECT 

FROM sicmed_standardprescription_doctor a

WHERE a.doctor_code = #{sicmedUser.id} 
	AND a.asked_code = #{askedClass.id}

SELECT 

FROM sicmed_standardprescription_doctor a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sicmed_standardprescription_doctor a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sicmed_standardprescription_doctor SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
