mapper.select:
SELECT 

FROM sicmed_standerprescription_asked a

WHERE a.id = #{id}

SELECT 

FROM sicmed_standerprescription_asked a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.standerprescription_code = #{sicmedStanderprescription.id}



		AND a.order_code = #{orderCode}


		AND a.asked_class_code = #{sicmedAskedClass.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sicmed_standerprescription_asked a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.standerprescription_code = #{sicmedStanderprescription.id}


		AND d.prescription_name LIKE CONCAT('%',#{sicmedStanderprescription.prescriptionName},'%')


		AND i.disease_name LIKE CONCAT('%',#{sicmedStanderprescription.sicmedDisease.diseaseName},'%')


		AND a.asked_class_code = #{sicmedAskedClass.id}


		AND b.asked_calss LIKE CONCAT('%',#{sicmedAskedClass.askedCalss},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sicmed_standerprescription_asked a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.standerprescription_code = #{sicmedStanderprescription.id}



		AND d.prescription_name = #{sicmedStanderprescription.prescriptionName}



		AND i.disease_name = #{sicmedStanderprescription.sicmedDisease.diseaseName}


		AND a.asked_class_code = #{sicmedAskedClass.id}



		AND b.asked_calss = #{sicmedAskedClass.askedCalss}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sicmed_standerprescription_asked a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sicmed_standerprescription_asked SET 
	amedical_use_code = #{sicmedMedicalUse.id},	
	update_by = #{updateBy.id},
	update_date = #{updateDate},
	remarks = #{remarks}
WHERE id = #{id}

UPDATE sicmed_standerprescription_asked SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
