mapper.select:
SELECT 

FROM sicmed_standerprescription_disease a

WHERE a.id = #{id}

SELECT 

FROM sicmed_standerprescription_disease a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.prescription_name = #{prescriptionName}


		AND a.prescription_name_ex = #{prescriptionNameEx}


		AND a.create_user = #{createUser.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sicmed_standerprescription_disease a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sicmed_standerprescription_disease SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
