mapper.select:
SELECT

FROM sicmed_word a

WHERE a.id = #{id}

SELECT

FROM sicmed_word a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.word_uri = #{wordUri}

SELECT

FROM sicmed_word a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.word_uri = #{wordUri}


		AND a.print_type = #{printType}


		AND a.print_loc = #{printLoc}


		AND a.create_by = #{createUser.id}


		AND a.update_by = #{updateUser.id}


		AND a.patient_id = #{sicmedPatient.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM sicmed_word a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sicmed_word SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
